var reconnect_enabled = false;
var start_enabled = false;
var stop_enabled = false;

if (msg.payload.fsm_state == "DISCONNECTED") {
    reconnect_enabled = true;
}
if (msg.payload.fsm_state == "OFF"){
    start_enabled = true;
}
if (msg.payload.fsm_state == "ON" ||
