return { enabled: setv_enabled };

var hv_on_enabled = true;
var hv_off_enabled = true;
var lv_on_enabled = true;
var lv_off_enabled = true;
var channel_selector_enabled = true;
var channel_enable_enabled = true;
var channel_disable_enabled = true;

var lv_on = (msg.payload.lv == 1);
var hv_on = (msg.payload.hv == 1);

var MAX_CHANNELS = 4;

if (!lv_on) {
    lv_off_enabled = false;
    hv_on_enabled = false;
    hv_off_enabled = false;
}
if (lv_on) {
    lv_on_enabled = false;
}
if (!hv_on) {
    hv_off_enabled = false;
}
if (hv_on) {
    lv_off_enabled = false;
    hv_on_enabled = false;
}

if (msg.payload.nChannels >= MAX_CHANNELS) {
    channel_enable_enabled = false;
}
if (msg.payload.hv == 1) {
    channel_selector_enabled = false;
    channel_enable_enabled = false;
    channel_disable_enabled = false;
}


return [
    { enabled: lv_on_enabled }, 
    { enabled: lv_off_enabled },
