    { enabled: channel_selector_enabled },
    { enabled: channel_enable_enabled },
    { enabled: channel_disable_enabled },
    ];

var status = msg.payload.channels;

var n_chan = 48;

var i;
var hi = 0x100000000;
var output = "";
var nChannels = 0;

// javascript bitwise operations are done on 32 bit integers,
// so we need to handle the first 32 bits 
// and the remaining 16 bits separately
// (the variable is originally a 64 bit float)

for (i = 0; i < 32; i++) {
  if ((status >>> i) & 1) {
      output += (i+1).toString() + ", ";
      nChannels += 1;
  }
}
status = status / hi;
for (i = 0; i < n_chan-32; i++) {
  if ((status >>> i) & 1) {
      output += (32+i+1).toString() + ", ";
      nChannels += 1;
  }
}
output = output.slice(0, -2);

msg.payload.channelOutput = output;
msg.payload.nChannels = nChannels;

return msg;

