var reconnect_enabled = false;
var clear_enabled = false;
var start_chiller_enabled = false;
var stop_chiller_enabled = false;
var start_co2_enabled = false;
var stop_co2_enabled = false;

if (msg.payload.fsm_state == "ALARM") {
    clear_enabled = true;
}
if (msg.payload.fsm_state == "DISCONNECTED") {
    reconnect_enabled = true;
}
if (msg.payload.fsm_state == "CONNECTED") {
    start_chiller_enabled = true;
}
if (msg.payload.fsm_state == "CHILLER_RUNNING") {
    stop_chiller_enabled = true;
    start_co2_enabled = true;
}
if (msg.payload.fsm_state == "CO2_RUNNING") {
    stop_co2_enabled = true;
}

return [
    { enabled: reconnect_enabled }, 
    { enabled: clear_enabled }, 
    { enabled: start_chiller_enabled }, 
    { enabled: stop_chiller_enabled },
    { enabled: start_co2_enabled },
    { enabled: stop_co2_enabled}
    ];

if (msg.payload.ext_is_used) {
    msg.payload = "useExt";
} else {
    msg.payload = "useInt";
}
return msg;
